sp_widget.client_script:
function($scope, $rootScope,spUtil) {
  /* widget controller */
  var c = this;
	c.ritmresults=[];
	c.submit=function(){
		var splitted = c.ritmlist.split(/[\n,]/);
		c.server.get({
        action: "submit",
        ritms: splitted,
				ritmdates: c.ritmdate
      }).then(function (response) {			

				if(response.data.results.length == 0){
					c.ritmresults = ['empty'];
				}
			else{
				 c.ritmresults = response.data.results;
			}
				c.duedate = c.ritmdate.toString();
      });
	};

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	//if(input && input.ritmdate && input.ritmlist)
	if(input && input.action === "submit"){
		var ritminput = input.ritms.join(',');
		data.results = [];
		var today = new GlideDateTime();
		var ritmdate = GlideDateTime(input.ritmdates);
		//ritmdate.addSeconds(25200);
		if(today.after(ritmdate)){
			var ritmQR = new GlideRecord('sc_req_item');
			ritmQR.addQuery('numberIN' + input.ritms.join(','));
			ritmQR.query();
			while (ritmQR.next()) {
				var ritmObj = {};
				ritmQR.setValue('due_date',ritmdate);
				//ritmQR.setValue('state', 3)
				$sp.getRecordDisplayValues(ritmObj, ritmQR, 'number, state, cat_item, due_date, quantity');
				data.results.push(ritmObj);
				ritmQR.update();

			}
			var taskQR = new GlideRecord('sc_task');
			taskQR.addQuery('request_item.numberIN' + input.ritms.join(','));
			taskQR.query();
			while (taskQR.next()) {
				var taskObj = {};
				taskQR.setValue('state',3)
				$sp.getRecordDisplayValues(taskObj, taskQR, 'number, state, request');
				data.results.push(taskObj);
				taskQR.update();
			}
		}
		else{
			data.results.push("date in future");
		}
		console.log(data.results);
		return;
	}

})();
